
// Article Model
class Article {
  final String title;
  final String thumbnail;
  final String content;
  final String createdAt;
  final String author;
  final bool isFavorite;

  Article({
    required this.author,
    required this.content,
    required this.createdAt,
    required this.thumbnail,
    required this.title,
    required this.isFavorite,
  });
}

